import React, { useRef, useState } from "react";
import { createStore } from "redux";
import { Provider } from "react-redux";
import ToDoList from "./ToDoList";
import UserInput from "./UserInput";
import ShowItems from "./ShowItems"
console.clear();

function reducer(state = { items: [] , excludeItems: items }, action) {
  if (action.type === "AddItem") {
    return { ...state, items: state.items.concat(action.item) };
  }
  if (action.type === "DeleteItem") {
    const newItems = state.items.filter((item) => item.id !== action.item.id);
    return { ...state, items: newItems };
  }
  if (action.type === "Toggle") {
    const newItems = state.items.map((item) => {
      if (item.id === action.item.id) {
        item.done = !item.done;
      }
      return item;
    });
    return { ...state, items: newItems };
  }
  if(action.type === "ShowUndoneCheckboxToggled"){
    if(action.event){
      const itemsUndone = state.items.filter(item => !item.done)
      const itemsDone = state.items.filte
    }
  }
  return state;
}

const store = createStore(reducer);


export default function App() {
  console.log("-App");
  return (
    <div className="App text-center">
      <Provider store={store}>
        {/* <ItemStatus /> */}
        <ShowItems />
        <UserInput />
        <ToDoList />
      </Provider>
    </div>
  );
}
